    public void findPath2(int x1,int y1, int x2,int y2){
          s.push(new StatePosition(x1,y1,x2,y2));
          while(!s.empty()&&!flag2){
            
          StatePosition sp =(StatePosition) s.pop();
          a[sp.x1][sp.y1][sp.x2][sp.y2]=1;
          if(sp.x1==2&&sp.y1==19&&sp.x2==12&&sp.y2==17){
          flag2=true;
           System.out.println(count);
          }
            if(s.empty()&&!flag2){
              System.out.println("can not find a path");
              }
          System.out.println(sp.x1+" "+sp.y1+" "+sp.x2+" "+sp.y2);
          count++;
          if(canGoTo(sp.x1+1,sp.y1,sp.x2,sp.y2)){
            
          s.push(new StatePosition(sp.x1+1,sp.y1,sp.x2,sp.y2));
          
          }
          if(canGoTo(sp.x1-1,sp.y1,sp.x2,sp.y2)){
          s.push(new StatePosition(sp.x1-1,sp.y1,sp.x2,sp.y2));
          }
          if(canGoTo(sp.x1,sp.y1+1,sp.x2,sp.y2)){
          s.push(new StatePosition(sp.x1,sp.y1+1,sp.x2,sp.y2));
          }
          if(canGoTo(sp.x1,sp.y1-1,sp.x2,sp.y2)){
          s.push(new StatePosition(sp.x1,sp.y1-1,sp.x2,sp.y2));
          }
          if(canGoTo(sp.x1,sp.y1,sp.x2+1,sp.y2)){
          s.push(new StatePosition(sp.x1,sp.y1,sp.x2+1,sp.y2));
          }
          if(canGoTo(sp.x1,sp.y1,sp.x2-1,sp.y2)){
          s.push(new StatePosition(sp.x1,sp.y1,sp.x2-1,sp.y2));
          }
          if(canGoTo(sp.x1,sp.y1,sp.x2,sp.y2+1)){
          s.push(new StatePosition(sp.x1,sp.y1,sp.x2,sp.y2+1));
          }
          if(canGoTo(sp.x1,sp.y1,sp.x2,sp.y2-1)){
          s.push(new StatePosition(sp.x1,sp.y1,sp.x2,sp.y2-1));
          }
          
    
    }}
    public boolean canGoTo(int x1,int y1, int x2,int y2){
  
    if(maze[x1][y1]==1&&maze[x2][y2]==1&&a[x1][y1][x2][y2]!=1&&(abs(x1-x2)+abs(y1-y2)) >= r){
     return true;
     }
     return false;
    }
    

